  //       setPost(response.data);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // }, []);

  return (
    <Base>
      <div className="pt-5">ProfileInfo</div>
      <h1>Welcome {user.name} </h1>
      <h1>email {user.email}</h1>
      <h1>about {user.about}</h1>
      <h1>All posts By you so far( {postContent.length} )</h1>

      {/* //Test API */}
      {/* <div>
        <ol>
          {
