import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import styled from "styled-components";
import { Container, Form } from "reactstrap";
import Base from "../components/Base";

// npm i @emailjs/browser

const Service = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "replace with service id",
        "replace with template id",
        form.current,
        "replace with user id"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <Base>

    <Container className="d-f">
      <Form>
    <StyledContactForm>
      <form ref={form} onSubmit={sendEmail}>
        <h1 style={{color:"Green"}} >Contact Us</h1>
        <h3 style={{color:"blue",backgroundColor:"yellow"}}>Contact the Help Team</h3>
        <label>Name</label>
        <input type="text" name="user_name" />
        <label>Email address associated with your Blogs account </label>
        <input type="email" name="user_email" />
        <label>Subject</label>
        <input type="text" name="user_subject"/>
        <label>Message</label>

        <textarea name="message" />
        <input type="submit" value="Send" />
      </form>
    </StyledContactForm>
    </Form>
    </Container>
    </Base>
  );
};

export default Service;

// Styles
const StyledContactForm = styled.div`
  width: 320px ;

  form {
    margin-left:160px;
    margin-right:160px;
    display: flex;
    align-items: flex-start;
    flex-direction: column;
    width: 200%;
    font-size: 16px;
    input {
      width: 100%;
      height: 35px;
      padding: 7px;
      outline: none;
      border-radius: 5px;
      border: 1px solid rgb(220, 220, 220);
      &:focus {
        border: 2px solid rgba(0, 206, 158, 1);
      }
    }

    textarea {
      max-width: 100%;
      min-width: 100%;
      width: 100%;
      max-height: 100px;
      min-height: 100px;
      padding: 7px;
      outline: none;
      border-radius: 5px;
      border: 1px solid rgb(220, 220, 220);
      &:focus {
        border: 2px solid rgba(0, 206, 158, 1);
      }
    }
    label {
      margin-top: 1rem;
    }
    input[type="submit"] {
      margin-top: 2rem;
      cursor: pointer;
      background: rgb(249, 105, 14);
      color: white;
      border: none;
    }

}

`;
