    //                     error.errors?.response?.data?.about ? true : false
    //                   }
    //                 />

    //                 <FormFeedback>
    //                   {error.errors?.response?.data?.about}
    //                 </FormFeedback>
    //               </FormGroup>

    //               <Container className="text-center">
    //                 <Button outline color="light">
    //                   Register
    //                 </Button>
    //                 <Button
    //                   onClick={resetData}
    //                   color="secondary"
    //                   type="reset"
    //                   className="ms-2"
    //                 >
    //                   Reset
    //                 </Button>
    //               </Container>
    //             </Form>
    //           </CardBody>
    //         </Card>
    //       </Col>
    //     </Row>
