import Base from "../components/Base";
import { getCurrentUserDetail} from "../auth";

const About = () => {

    const [user, setUser] = useState(undefined);

    useEffect(() => {
        setUser(getCurrentUserDetail());
      }, []);

    return (
        <Base>        
            <h1>AboutUs Page</h1> 
            <h1>{user.name}</h1>
        </Base>
    );
  };

  export default About;
