import React, { useContext, useEffect, useState } from "react";
import { Router } from "react-router-dom";
import { getCurrentUserDetail, isLoggedIn } from "../../auth";
import Base from "../../components/Base";
import Post from "../../components/Post";
import userContext from "../../context/userContext";
import { getPostByUserId } from "../../services/post-service";
import axios from "axios";

const ProfileInfo = () => {
  const user = useContext(userContext);
  console.log(user);

  const [postContent, setPostContent] = useState([]);

  useEffect(() => {
    getPostByUserId(user).then((data) => {
      console.log(data);
      setPostContent(data);
    });
  }, []);
  const [posts, setPost] = useState([]);

  useEffect(() => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        console.log(response.data);
        setPost(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <Base>
      <div className="pt-5">ProfileInfo</div>
      <h1>Welcome {user.name} </h1>
      <h1>email {user.email}</h1>
      <h1>about {user.about}</h1>
      <h1>All posts By you so far</h1>
      {/* <div>
        <ol>
          {
            //map is used for iterating
            posts.map((post) => (
              <li key={post.id}>{post.title}<button>delete</button></li>
             // <li>{post.title}</li>
            ))
          }
        </ol>
      </div> */}

      <div>
        <ol>
          {
            //map is used for iterating
            postContent.map((post) => (
              <li key={post.id}>
                {post.title}
                <button>delete</button>
              </li>
              // <li>{post.title}</li>
            ))
          }
        </ol>
      </div>

      {/* <div>
      {postContent.map(data => (
        <p>{data.postId}</p>
      ))} */}
      {/* </div> */}
    </Base>
  );
};

export default ProfileInfo;
