        name:'Siddhesh'
    })
//    useEffect(()=>{
//        setUser({
//            name:'snehal patil'
//        })
//    },[])
    
    return(
        <userContext.Provider value={user}>
            {children}
        </userContext.Provider>
    )
}

export default UserProvider
