import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import {
  Row,
  Col,
  Pagination,
  PaginationItem,
  PaginationLink,
  Container,
} from "reactstrap";
import Base from "../components/Base";
import { loadPostByCat } from "../services/post-service";
const Services = () => {
  //defining postContent
  const [postContent, setPostContent] = useState();

  // to call a function for a first Time
  useEffect(() => {
    //load all posts from server
    loadPostByCat()
      .then((data) => {
        console.log(data);
        setPostContent(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const [currentPage, setCurrentPage] = useState(0)

  const changePage = (pageNumber = 0, pageSize = 5) => {
    if (pageNumber > postContent.pageNumber && postContent.lastPage) {
      return;
    }
    if (pageNumber < postContent.pageNumber && postContent.pageNumber == 0) {
      return;
    }
    loadPostByCat(pageNumber, pageSize)
      .then((data) => {
        setPostContent({
          content: [...postContent.content, ...data.content],
          totalPages: data.totalPages,
          totalElements: data.totalElements,
          pageSize: data.pageSize,
          lastPage: data.lastPage,
          pageNumber: data.pageNumber,
        });

        console.log(data);
      })
      .catch((error) => {
        toast.error("Error in loading posts");
      });
  };

  return (
    <Base>
      <div className="container-fluid pt-5 pe-1 ps-1">
        <Row>
          <Col
            md={{
              size: 10,
              offset: 1,
            }}
          >
            <h1>Blogs Count ( {postContent?.totalElements} )</h1>

            <Container className="mt-3">
              <Pagination size="lg">
                <PaginationItem
                  onClick={() => changePage(postContent.pageNumber - 1)}
                  disabled={postContent.pageNumber == 0}
                >
                  <PaginationLink previous>Previous</PaginationLink>
                </PaginationItem>

                {[...Array(postContent.totalPages)].map((item, index) => (
                  <PaginationItem
                    onClick={() => changePage(index)}
                    active={index == postContent.pageNumber}
                    key={index}
                  >
                    <PaginationLink>{index + 1}</PaginationLink>
                  </PaginationItem>
                ))}

                <PaginationItem
                  onClick={() => changePage(postContent.pageNumber + 1)}
                  disabled={postContent.lastPage}
                >
                  <PaginationLink next>Next</PaginationLink>
                </PaginationItem>
              </Pagination>
            </Container>
          </Col>
        </Row>
      </div>
    </Base>
  );
};

export default Services;
