import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import { Row, Col } from "reactstrap";
import { toast } from "react-toastify";
import InfiniteScroll from "react-infinite-scroll-component";
import Base from "../components/Base";
import Post from "../components/Post";
import { loadPostByCat } from "../services/post-service";
import "./Services.css";
import { Link,NavLink as ReactLink, useNavigate } from "react-router-dom";
const Science = () => {
  //defining postContent
  const [postContent, setPostContent] = useState({
    content: [],
    totalPages: "",
    totalElements: "",
    pageSize: "",
    lastPage: false,
    pageNumber: "",
  });

  
  

  // to call a function for a first Time
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    console.log("loading posts");
    console.log(currentPage);
    changePage(currentPage);
  }, [currentPage]);

  const changePage = (event , pageNumber = 0, pageSize = 5) => {

    
    console.log("******************************");  
    console.log(event);
    let x=1;
    switch (event) {
      case "Orange":
        x = 1;
        break;
      case "Radish":
        x = 2;
        break;
      case "Cherry":
        x = 2;
        break;
      default:
        x = 1;
    }

    if (pageNumber > postContent.pageNumber && postContent.lastPage) {
      return;
    }
    if (pageNumber < postContent.pageNumber && postContent.pageNumber == 0) {
      return;
    }
    console.log(x);
    loadPostByCat(x, pageNumber, pageSize)
      .then((data) => {
          console.log(data,"Before")
        setPostContent({
          content: [...postContent.content, ...data.content],
          totalPages: data.totalPages,
          totalElements: data.totalElements,
          pageSize: data.pageSize,
          lastPage: data.lastPage,
          pageNumber: data.pageNumber,
        });

        console.log(setPostContent,"After");
      })
      .catch((error) => {
        toast.error("Error in loading posts");
      });
      
  };

  const changePageInfinite = () => {
    console.log("page chagned");
    setCurrentPage(currentPage + 1);
  };

  const getInitialState = () => {
    const value = "Orange";
    return value;
  };

  const [value, setValue] = useState(getInitialState);

  const handleChange = (e) => {
    setValue(e.target.value);
    console.log("XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX");
    console.log(e.target.value);
    changePage(e.target.value);
  };

  return (
    <Base>
      <div className="container-fluid pt-5 pe-1 ps-1">
        <Row>
          <Col
            md={{
              size: 10,
              offset: 1,
            }}
          >
            <div className="d-flex categorySelect">
              <h1>
                {value} ( {postContent?.totalElements} )
              </h1>
              <select
                value={value}
                onChange={handleChange}
                class="rounded col-sm-1 categoryDrop "
                aria-label="Default select example"
              >
                <option value="Orange" 
                tag={ReactLink} to="/technology"
                >Orange</option>
                <option value="Radish">Radish</option>
                <option value="Cherry">Cherry</option>
              </select>
            </div>

            <InfiniteScroll
              dataLength={postContent.content.length}
              next={changePageInfinite}
              hasMore={!postContent.lastPage}
              loader={<h4>Loading...</h4>}
              endMessage={
                <p style={{ textAlign: "center" }}>
                  <b>Yay! You have seen it all</b>
                </p>
              }
            >
              {postContent.content.map((post, index) => (
                <Post post={post} key={index} />
              ))}
            </InfiniteScroll>
          </Col>
        </Row>
      </div>
    </Base>
  );
};

export default Science;
