import Base from "../components/Base";
import {
  Label,
  Card,
  CardBody,
  CardHeader,
  Col,
  Container,
  Form,
  FormGroup,
  Input,
  Row,
  Button,
  CardFooter,
} from "reactstrap";
import { Link, NavLink as ReactLink, useNavigate } from "react-router-dom";
const styles = StyleSheet.create({
    heading: {
      fontSize: 18,
      fontWeight: '600',
      marginBottom: 13,
    },
    card: {
      backgroundColor: 'white',
      borderRadius: 8,
      paddingVertical: 45,
      paddingHorizontal: 25,
      width: '100%',
      marginVertical: 10,
    },
    shadowProp: {
      shadowColor: '#171717',
      shadowOffset: {width: -2, height: 4},
      shadowOpacity: 0.2,
      shadowRadius: 3,
    },
  });
const Login = () => {
  return (
    // <Base>
    <Container >

      <Row className="mt-4">
        <Col
          sm={{
            size: 6,
            offset: 0,
          }}
        >
            <Container className="mt-5">
                <br></br><br></br><br></br><br></br><br></br>
                <Link style={{ textDecoration: "none" }} tag={ReactLink} to="/">
          <h1 className="text-warning">MyBlog</h1>
          </Link>
          <p>
            <b>it helps you share your Knowledge,with the world.</b>
          </p>
          </Container>
        </Col>
        <Col
        //   sm={{
        //     size: 5,
        //     offset: 7,
        //   }}
        >
            <Container className="mt-5 col-11">
            <br></br><br></br>
          <Card >
            <CardBody>
              <Form>
                {/* Email field */}
                <br></br>

                <FormGroup>
                  <Input type="text" id="email" placeholder="Email address" />
                </FormGroup>
              

                {/* password field */}

                <FormGroup>
                  <Input type="password" id="password" placeholder="Password" />
                </FormGroup>

                <Container className="text-center">
                  <Button color="success">Login</Button>
                  <Button className="ms-2" color="secondary">
                    Reset
                  </Button>
                  <br></br>
                </Container>
              </Form>
            </CardBody>
            <CardFooter>
              <Container className="text-center" >
              
                <p>Don't have accout yet?</p>
                <Button color="warning">Create New Account</Button>
                <h6>&nbsp;</h6>
              </Container>
            </CardFooter>
          </Card>
          </Container>
        </Col>
      </Row>
    </Container>
    //</Base>
  );
};

export default Login;
