import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import { loadAllPosts } from '../services/post-service'
import { Row, Col, Pagination, PaginationItem, PaginationLink, Container } from 'reactstrap'
import Post from './Post'
import { toast } from 'react-toastify'
import InfiniteScroll from 'react-infinite-scroll-component'
function NewFeed() {


    const [postContent, setPostContent] = useState({
        content: [],
        totalPages: '',
        totalElements: '',
        pageSize: '',
        lastPage: false,
        pageNumber: ''

    })

    const [currentPage, setCurrentPage] = useState(0)

    useEffect(() => {
        console.log("loading posts")
        console.log(currentPage)
        changePage(currentPage)

    }, [currentPage])


    const changePage = (pageNumber = 0, pageSize = 5) => {

        if (pageNumber > postContent.pageNumber && postContent.lastPage) {
            return
        }
        if (pageNumber < postContent.pageNumber && postContent.pageNumber == 0) {
            return
        }
        loadAllPosts(pageNumber, pageSize).then(data => {


            setPostContent({
                content: [...postContent.content, ...data.content],
                totalPages: data.totalPages,
                totalElements: data.totalElements,
                pageSize: data.pageSize,
                lastPage: data.lastPage,
                pageNumber: data.pageNumber
            })

            console.log(data);

        }).catch(error => {
            toast.error("Error in loading posts")

        })
    }

    const changePageInfinite = () => {
        console.log("page chagned")
        setCurrentPage(currentPage + 1)

    }

    return (



        <div className="container-fluid pt-5 pe-1 ps-1">
            <Row>
                <Col md={
                    {
                        size: 10,
                        offset: 1
                    }
                }>

<div className="d-flex ">
              <Dropdown
                isOpen={dropdownOpen}
                toggle={toggle}

              >
                <DropdownToggle caret color="white"  className="categorySelect">Blogs Count ( {postContent?.totalElements} )</DropdownToggle>
                <DropdownMenu className="dropMenu" >
                  <DropdownItem header>Science</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/">Science</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/technology">Technology</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/personalblogs">Personal blogs</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/educational">Educational</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/financial">Financial</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/travel">Travel</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/health">Health</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/food">Food</DropdownItem>
                  <DropdownItem tag={ReactLink} to="/sports">Sports</DropdownItem>
                </DropdownMenu>
              </Dropdown>
            </div>

                    <InfiniteScroll
                        dataLength={postContent.content.length}
                        next={changePageInfinite}
                        hasMore={!postContent.lastPage}
                        loader={<h4>Loading...</h4>}
                        endMessage={
                            <p style={{ textAlign: 'center' }}>
                              <b>Yay! You have seen it all</b>
                            </p>
                          }


                    >


                        {
                            postContent.content.map((post, index) => (
                                <Post post={post} key={index} />
                            ))
                        }



                    </InfiniteScroll>


                    {/* <Container className='mt-3'>

                        <Pagination size='lg'>
                            <PaginationItem onClick={() => changePage(postContent.pageNumber-1)} disabled={postContent.pageNumber == 0}>
                                <PaginationLink previous>
                                    Previous
                                </PaginationLink>
                            </PaginationItem>

                            {
                                [...Array(postContent.totalPages)].map((item, index) => (


                                    <PaginationItem onClick={() => changePage(index)} active={index == postContent.pageNumber} key={index}>
                                        <PaginationLink>

                                            {index + 1}

                                        </PaginationLink>
                                    </PaginationItem>

                                ))
                            }


                            <PaginationItem onClick={() => changePage(postContent.pageNumber+1)} disabled={postContent.lastPage}>
                                <PaginationLink next>
                                    Next
                                </PaginationLink>
                            </PaginationItem>
                        </Pagination>

                    </Container> */}






                </Col>
            </Row>
        </div>


    )
}

export default NewFeed
